import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import httpClient from "../httpClient";

const AddRoom = () => {
  const [roomName, setRoomName] = useState("");
  const [createdBy, setCreatedBy] = useState("");
  const [user, setUser] = useState({});
  const [users, setUsers] = useState([]);
  const [selectedUsers, setSelectedUsers] = useState([]);
  const [memberSelection, setMemberSelection] = useState(false);

  const navigate = useNavigate();

  useEffect(() => {
    (async () => {
      try {
        const resp = await httpClient.get(
          "//localhost:5000/contract/api/get_user"
        );
        setUser(resp.data);
      } catch (error) {
        console.log("Not authenticated");
      }
    })();

    (async () => {
      try {
        const resp = await httpClient.get(
          "//localhost:5000/contract/api/get_all_users"
        );
        // console.log(resp.data.response);
        setUsers(resp.data.response);
      } catch (error) {
        console.log("Not authenticated");
      }
    })();
    if (user) {
      setCreatedBy(user.email);
    }
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      await httpClient.post("//localhost:5000/contract/api/addroom", {
        roomName,
        createdBy: user.email,
      });

      navigate("/");
    } catch (error) {
      if (error.response.status === 401) {
        alert("Record can't be inserted!");
      }
    }
  };
  return (
    <div>
      <h2>Add Room</h2>
      <h2>Add Room Member</h2>
      <table>
        {users && (
          <>
            {users.map((x, index) => {
              return (
                <>
                  <tr key={index}>
                    <td>{x.id}</td>
                    <td>{x.email}</td>
                    <td>
                      <input
                        name="user"
                        value={memberSelection}
                        type="checkbox"
                        onChange={(e) => {
                          setMemberSelection(e.target.checked);
                          const data = {
                            id: x.id,
                            email: x.email,
                            value: e.target.checked,
                          };
                          if (data.value === true) {
                            selectedUsers.push(data);
                          } else {
                            const filterdata = selectedUsers.filter(
                              (x) => x.id !== data.id && x.email !== data.email
                            );
                            setSelectedUsers(filterdata);
                          }
                        }}
                      />
                    </td>
                  </tr>
                </>
              );
            })}
          </>
        )}
      </table>
      <form>
        <button onClick={handleSubmit}>Submit</button>
      </form>

      <form>
        <div>
          <label>Room Name :</label>
          <input
            name="roomName"
            type="text"
            value={roomName}
            onChange={(e) => setRoomName(e.target.value)}
          />
        </div>
        <div>
          <label>Created By:</label>
          <input
            name="createdBy"
            type="text"
            value={user && user.email}
            onChange={(e) => setCreatedBy(e.target.value)}
          />
        </div>
        <button onClick={handleSubmit}>Submit</button>
      </form>
    </div>
  );
};

export default AddRoom;
