import React, { useState, useEffect } from "react";
import { Link, useParams } from "react-router-dom";
import httpClient from "../httpClient";
const Room = () => {
  const [roomMember, setRoomMember] = useState([]);

  const param = useParams();

  useEffect(() => {
    (async () => {
      try {
        const resp = await httpClient.get(
          `//localhost:5000/contract/api/get_room_members/${param.id}`
        );
        setRoomMember(resp.data.response);
      } catch (error) {
        console.log("No room member available");
      }
    })();
  }, []);

  return (
    <>
      <h2>List of Room Member</h2> <br />
      <table>
        <tr>
          <td>
            <b>ID</b>
          </td>
          <td>
            <b>Name</b>
          </td>
          <td>
            <b>Actions</b>
          </td>
        </tr>
        {roomMember && (
          <>
            {roomMember.map((x, index) => {
              return (
                <>
                  <tr key={x.id}>
                    <td>{x.id}</td>
                    <td>{x.member_name}</td>
                    <td><button></button></td>
                  </tr>
                </>
              );
            })}
          </>
        )}
      </table>
      <Link to={`/addmember/${param.id}`}>Add Member</Link>
      <br />
      <Link to={`/`}>Back to Room</Link>
    </>
  );
};

export default Room;
