import React, { useState, useEffect } from "react";
import { Link, useParams } from "react-router-dom";
import httpClient from "../httpClient";
const Room = () => {
  const [roomMember, setRoomMember] = useState([]);
  const [users, setUsers] = useState([]);

  const param = useParams();

  const getAllUsers = async () => {
    try {
      const resp = await httpClient.get(
        `//localhost:5000/contract/api/get_all_user`
      );
      setUsers(resp.data);
    } catch (error) {
      console.log("No room member available");
    }
  };
  useEffect(() => {
    getAllUsers()(async () => {
      try {
        const resp = await httpClient.get(
          `//localhost:5000/contract/api/get_room_members/${param.id}`
        );
        console.log(resp.data.response);
        setRoomMember(resp.data.response);
      } catch (error) {
        console.log("No room member available");
      }
    })();
  }, []);

  const deleteMember = async (memberid) => {
    if (memberid) {
      try {
        const resp = await httpClient.get(
          `//localhost:5000/contract/api/delete_room_member/${memberid}`
        );
        window.location.reload(false);
      } catch (error) {
        console.log("No room member available");
      }
    }
  };

  return (
    <>
      <h2>List of Room Member</h2> <br />
      <table>
        <tr>
          <td>
            <b>ID</b>
          </td>
          <td>
            <b>User</b>
          </td>
          <td>
            <b>Email</b>
          </td>

          <td>
            <b>Actions</b>
          </td>
        </tr>
        {roomMember.length === 0 && <p>No member is available</p>}
        {roomMember && (
          <>
            {roomMember.map((x, index) => {
              return (
                <>
                  <tr key={x.id}>
                    <td>{x.id}</td>
                    <td>{x.user_id}</td>
                    <td>{users && users.filter(a=a.id===x.user_id).map(item=>{
                      return(
                        <li>{}</li>
                      )
                    }) }</td>
                    <td>
                      <button onClick={(e) => deleteMember(x.id)}>X</button>
                    </td>
                  </tr>
                </>
              );
            })}
          </>
        )}
      </table>
      <Link to={`/addmember/${param.id}`}>Add Member</Link>
      <br />
      <Link to={`/`}>Back to Room</Link>
    </>
  );
};

export default Room;
