import React, { useState, useEffect } from "react";
import io from "socket.io-client";
import { Link, useNavigate, useParams } from "react-router-dom";
import httpClient from "../httpClient";

const socket = io("http://127.0.0.1:5000/");

const Chat = () => {
  const [member, setMember] = useState([]);
  const [message, setMessage] = useState("");
  const [user, setUser] = useState("");
  const [chat, setChat] = useState([]);

  const params = useParams();

  const getRoomMemberId = async (id) => {
    try {
      const res = await httpClient.get(
        `//localhost:5000/contract/api/get_room_members/${id}`
      );

      const result = res.data.response;
      const filtered = result.filter((x) => x.user_id === params.userid);
      // console.log(filtered);
      setMember(filtered);
    } catch (error) {
      console.log("No room is available");
    }
  };

  const getChatMessages = async (id) => {
    try {
      const res = await httpClient.get(
        `//localhost:5000/contract/api/chat/${id}`
      );
      console.log(res.data.response);
      const result = res.data.response;

      setChat(result);
    } catch (error) {
      console.log("No message is available");
    }
  };

  const getUserEmail = async () => {
    try {
      const res = await httpClient.get(
        `//localhost:5000/contract/api/get_all_users`
      );
      console.log(res.data.response);
      const result = res.data.response;

      setUser(result);
    } catch (error) {
      console.log("No room is available");
    }
  };

  useEffect(() => {
    getRoomMemberId(params.roomid);
    getChatMessages(params.roomid);
    getUserEmail(params.userid);
  }, []);

  useEffect(() => {
    socket.on("chat", (payload) => {
      setChat([...chat, payload]);
    });

    socket.on("connect", function () {
      socket.emit("join_room", {
        user: params.userid,
        room: params.roomid,
      });
    });

    //join room announcement

    // socket.on("join_room_announcement", function (data) {
    //   console.log(message1);
    //   setMessage1([...message1, data.user]);
    //   // setMessage1([...message1, data.user]);
    //   // const data = `${payload.user} join the room`;
    //   // console.log(data);
    //   // setMessage1(data);
    // });
  });

  const handleSubmit = async (e) => {
    e.preventDefault();

    const room_id = params.roomid;
    getRoomMemberId(room_id);

    if (member && message !== "") {
      // console.log(member);
      const room_member_id = member[0].id;
      const user_id=member[0]
      socket.emit("chat", { room_id, room_member_id, message });
      setMessage("");
    }

    // try {
    //   const res = await httpClient.post(
    //     `//localhost:5000/contract/api/addmessage`,
    //     data
    //   );
    // } catch (error) {
    //   console.log("No message available");
    // }

    // setMessage([]);
    // window.location.reload(false);
  };
  return (
    <div>
      {member.length !== 0 ? (
        <>
          <h4>Collaboration Interface</h4>
          {console.log(user)}
          <table>
            {chat && (
              <>
                {chat.map((x, index) => {
                  return (
                    <>
                      <tr key={index}>
                        <td>
                          <p>
                            <b>
                              {x.room_member_id} {user.email}
                            </b>{" "}
                            : {x.message}
                          </p>
                        </td>
                      </tr>
                    </>
                  );
                })}
              </>
            )}
          </table>
          <form>
            <div>
              <input
                name="message"
                type="text"
                placeholder="Enter your message"
                value={message}
                onChange={(e) => setMessage(e.target.value)}
              />
              <button onClick={handleSubmit}>Submit</button>
            </div>
          </form>
        </>
      ) : (
        <p>
          you are not invited: <Link to={`/`}>Back</Link>
        </p>
      )}
    </div>
  );
};

export default Chat;
