import React, { useEffect, useState } from "react";
import { useNavigate, useParams } from "react-router-dom";
import httpClient from "../httpClient";

const AddRoom = () => {
  const [memberName, setMemberName] = useState("");
  const [addedBy, setAddedBy] = useState("");
  const [user, setUser] = useState({});
  const [users, setUsers] = useState([]);

  const param = useParams();
  console.log(param);

  const navigate = useNavigate();

  useEffect(() => {
    (async () => {
      try {
        const resp = await httpClient.get(
          "//localhost:5000/contract/api/get_user"
        );
        setUser(resp.data);
      } catch (error) {
        console.log("Not authenticated");
      }
    })();

    (async () => {
      try {
        const resp = await httpClient.get(
          "//localhost:5000/contract/api/get_all_user"
        );
        setUsers(resp.data);
      } catch (error) {
        console.log("Not authenticated");
      }
    })();
    if (user) {
      setAddedBy(user.email);
    }
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();

    // try {
    //   await httpClient.post("//localhost:5000/contract/api/addmember", {
    //     user_id: user.id,
    //     room_id: param.id,
    //     addedBy: user.email,
    //   });

    //   navigate("/roommembers");
    // } catch (error) {
    //   if (error.response.status === 401) {
    //     alert("Record can't be inserted!");
    //   }
    // }
  };
  return (
    <div>
      <h2>Add Room Member</h2>
      <table>
        {users && (
          <>
            {users.map((x, index) => {
              return (
                <>
                  <tr key={x.id}>
                    <td>{x.id}</td>
                    <td>{x.room_name}</td>
                    <td>
                      <Link to={`/roommembers/${x.id}`}>Member Detail</Link>
                    </td>
                    <td>
                      <Link to={`/chat/${user.id}/${x.id}`}>Chat</Link>
                    </td>
                    <td>
                      <button onClick={(e) => deleteRoom(x.id)}>X</button>
                    </td>
                  </tr>
                </>
              );
            })}
          </>
        )}
      </table>
      <form>
        <div>
          <label>Member Name :</label>
          <input
            name="memberName"
            type="text"
            value={memberName}
            onChange={(e) => setMemberName(e.target.value)}
          />
        </div>
        <div>
          <label>Added By:</label>
          <input
            name="addedBy"
            type="text"
            value={user && user.email}
            onChange={(e) => setAddedBy(e.target.value)}
          />
        </div>
        <button onClick={handleSubmit}>Submit</button>
      </form>
    </div>
  );
};

export default AddRoom;
