import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import httpClient from "../../httpClient";

const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [stateErrors, setStateErrors] = useState({
    emailError: "",
    passwordError: "",
  });
  const navigate = useNavigate();

  const handleRegister = () => {
    navigate("/register");
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await httpClient.post("//localhost:5000/contract/api/login", {
        email,
        password,
      });

      navigate("/");
    } catch (error) {
      if (error.response.status === 401) {
        alert("Invalid credentials");
      }
    }
  };
  return (
    <div className="container-md">
      <h3 className="mb-3">Login</h3>
      <form>
        <div class="row col-sm-4">
          <label for="inputEmail" class="col-sm-3 col-form-label">
            Email
          </label>
          <div class="col-sm-8">
            <input
              name="email"
              type="email"
              className="form-control form-control-sm"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
          </div>
          <div className="">
            {stateErrors.emailError && stateErrors.emailError}
          </div>
        </div>

        <div class="row col-sm-4">
          <label for="inputEmail" class="col-sm-3 col-form-label">
            Password
          </label>
          <div class="col-sm-8">
            <input
              name="password"
              type="password"
              className="form-control form-control-sm"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
          </div>
        </div>

        <div class="row col-sm-4">
          <label for="inputEmail" class="col-sm-3 col-form-label"></label>
          <div class="col-sm-8">
            <button onClick={handleSubmit} className="btn btn-sm btn-success">
              Login
            </button>
            <button onClick={handleRegister} className="btn btn-sm btn-success">
              Register
            </button>
          </div>
        </div>
      </form>
    </div>
  );
};

export default Login;
