import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import httpClient from "../httpClient";

const LandingPage = () => {
  const [user, setUser] = useState({});
  const [room, setRoom] = useState([]);
  const [roomMember, setRoomMember] = useState([]);

  const logoutUser = async () => {
    await httpClient.post("//localhost:5000/logout");
    window.location.href = "/";
  };

  useEffect(() => {
    (async () => {
      try {
        const resp = await httpClient.get(
          "//localhost:5000/contract/api/get_user"
        );
        setUser(resp.data);
      } catch (error) {
        console.log("Not authenticated");
      }
    })();

    (async () => {
      try {
        const res = await httpClient.get(
          "//localhost:5000/contract/api/get_room"
        );
        console.log(res.data.response);

        setRoom(res.data.response);
      } catch (error) {
        console.log("No room is available");
      }
    })();

        // if (res.data.response) {
        //   console.log("hi");
        //   const id = res.data.response(async () => {
        //     try {
        //       const res = await httpClient.get(
        //         `//localhost:5000/contract/api/get_room_members/${id}`
        //       );
        //       console.log(res.data.response);
        //       setRoomMember(res.data.response);
        //     } catch (error) {
        //       console.log("No room is available");
        //     }
        //   })();
        // }


  }, []);

  const deleteRoom = async (id) => {
    if (id) {
      try {
        const resp = await httpClient.get(
          `//localhost:5000/contract/api/delete_room/${id}`
        );
        window.location.reload(false);
      } catch (error) {
        console.log("No room available");
      }
    }
  };
  return (
    <div>
      {user && (
        <>
          <h1>Welcome to collaboration tool</h1>
          {Object.keys(user).length !== 0 ? (
            <>
              <h2>Logged in</h2>
              <h3>ID: {user.id}</h3>
              <h3>Email: {user.email}</h3>
              <Link to="/addroom">Add Room</Link>
              <br />
              {room.length == 0 && <p>No room is available</p>}
              <table>
                <tr>
                  <td>
                    <b>ID</b>
                  </td>
                  <td>
                    <b>Name</b>
                  </td>
                  <td>
                    <b>Members</b>
                  </td>
                  <td>
                    <b>Chat</b>
                  </td>
                  <td>
                    <b>Actions</b>
                  </td>
                </tr>
                {room && roomMember && (
                  <>
                    {room.map((x, index) => {
                      return (
                        <>
                          <tr key={index}>
                            <td>{x.id}</td>
                            <td>{x.room_name}</td>
                            <td>
                              <Link to={`/roommembers/${x.id}`}>
                                Member Detail
                              </Link>
                            </td>
                            <td>
                              <Link
                                to={`/chat/${user.id}/${x.id}/${roomMember.id}`}
                              >
                                Chat
                              </Link>
                            </td>
                            <td>
                              <button onClick={(e) => deleteRoom(x.id)}>
                                X
                              </button>
                            </td>
                          </tr>
                        </>
                      );
                    })}
                  </>
                )}
              </table>

              <br />
              <button onClick={logoutUser}>Logout</button>
            </>
          ) : (
            <>
              <p>You are not logged in</p>
              <button>
                <Link to="/login">Login</Link>
              </button>
              <button>
                <Link to="/register">Register</Link>
              </button>
            </>
          )}
        </>
      )}
    </div>
  );
};

export default LandingPage;
