import React, { useState, useEffect } from "react";
import io from "socket.io-client";
import { useNavigate, useParams } from "react-router-dom";
import httpClient from "../httpClient";

const socket = io.connect("http://localhost:5000");

const Chat = () => {
  const [member, setMember] = useState([]);
  const [message, setMessage] = useState([]);
  const [chat, setChat] = useState([]);
  const navigate = useNavigate();
  const params = useParams();

  useEffect(() => {
    socket.on("chat", (payload) => {
      console.log(payload);
      setChat([...chat, payload]);
    });
    (async () => {
      try {
        const res = await httpClient.get(
          `//localhost:5000/contract/api/get_room_members/${params.roomid}`
        );
        console.log(res.data.response);
        const result = res.data.response;
        const filtered = result.filter((x) => x.user_id === params.userid);

        setMember(filtered);
      } catch (error) {
        console.log("No room is available");
      }
    })();


  }, []);

  const handleSubmit = (e) => {
    e.preventDefault();
    const userid = params.userid;
    const roomid = params.roomid;
    const roommemberid = member[0].id;

    console.log(chat);
    socket.emit("chat", { roomid, roommemberid, userid, message });
    setMessage([]);
  };
  return (
    <div>
      <h4>Collaboration Interface</h4>

      <table>
        {chat && (
          <>
            {chat.map((x, index) => {
              return (
                <>
                  <tr key={index}>
                    <td>
                      <p>
                        {x.room_member_id} :{x.message}
                      </p>
                    </td>
                  </tr>
                </>
              );
            })}
          </>
        )}
      </table>

      <form>
        <div>
          {/* <label>Enter your message:</label> */}
          <input
            name="message"
            type="text"
            value={message}
            onChange={(e) => setMessage(e.target.value)}
          />
          <button onClick={handleSubmit}>Submit</button>
        </div>
      </form>
    </div>
  );
};

export default Chat;
