import React, { useEffect, useState } from "react";
import { useNavigate, useParams } from "react-router-dom";
import httpClient from "../httpClient";

const AddRoom = () => {
  const [memberName, setMemberName] = useState("");
  const [memberSelection, setMemberSelection] = useState(false);
  const [addedBy, setAddedBy] = useState("");
  const [user, setUser] = useState({});
  const [users, setUsers] = useState([]);
  const [selectedUsers, setSelectedUsers] = useState([]);

  const param = useParams();
  // console.log(memberSelection);

  const navigate = useNavigate();

  useEffect(() => {
    (async () => {
      try {
        const resp = await httpClient.get(
          "//localhost:5000/contract/api/get_user"
        );
        setUser(resp.data);
      } catch (error) {
        console.log("Not authenticated");
      }
    })();

    (async () => {
      try {
        const resp = await httpClient.get(
          "//localhost:5000/contract/api/get_all_users"
        );
        // console.log(resp.data.response);
        setUsers(resp.data.response);
      } catch (error) {
        console.log("Not authenticated");
      }
    })();
    if (user) {
      setAddedBy(user.email);
    }
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    console.log(selectedUsers);

    // try {
    //   await httpClient.post("//localhost:5000/contract/api/addmember", {
    //     user_id: user.id,
    //     room_id: param.id,
    //     addedBy: user.email,
    //   });

    //   navigate("/roommembers");
    // } catch (error) {
    //   if (error.response.status === 401) {
    //     alert("Record can't be inserted!");
    //   }
    // }
  };

  const handleCheckbox = (id, email) => {
    setMemberSelection(!memberSelection);

    console.log(memberSelection);
    // console.log(selectedUsers);

    // const result = selectedUsers.filter((x) =>
    //   (x.id === id && x.email === email).map((a) => <li>{a}</li>)
    // );

    // console.log(result);

    if (memberSelection === true) {
      const data = {
        id: id,
        email: email,
      };
      selectedUsers.push(data);
    }
  };
  return (
    <div>
      <h2>Add Room Member</h2>
      <table>
        {users && (
          <>
            {users.map((x, index) => {
              return (
                <>
                  <tr key={x.id}>
                    <td>{x.id}</td>
                    <td>{x.email}</td>
                    <td>
                      <input
                        name="user"
                        value={memberSelection}
                        type="checkbox"
                        onChange={(e) => {
                          setMemberSelection(e.target.checked);
                          const data = {
                            id: x.id,
                            email: x.email,
                            value: e.target.checked,
                          };
                          if (data.value === true) {
                            selectedUsers.push(data);
                          } else {
                            const filterdata = selectedUsers.filter
                            selectedUsers.pop(data);
                          }
                        }}
                      />
                    </td>
                  </tr>
                </>
              );
            })}
          </>
        )}
      </table>
      <form>
        {/* <div>
          <label>Member Name :</label>
          <input
            name="memberName"
            type="text"
            value={memberName}
            onChange={(e) => setMemberName(e.target.value)}
          />
        </div>
        <div>
          <label>Added By:</label>
          <input
            name="addedBy"
            type="text"
            value={user && user.email}
            onChange={(e) => setAddedBy(e.target.value)}
          />
        </div> */}
        <button onClick={handleSubmit}>Submit</button>
      </form>
    </div>
  );
};

export default AddRoom;
