import React, { useState, useEffect } from "react";
import io from "socket.io-client";
import { useNavigate, useParams } from "react-router-dom";

const socket = io.connect("http://localhost:5000");

const Chat = () => {
  const [memberId, setMemberId] = useState("");
  const [message, setMessage] = useState([]);
  const [chat, setChat] = useState([]);
  const navigate = useNavigate();
  const params = useParams();

  useEffect(() => {
    socket.on("chat", (msg) => {
      console.log(msg);
      setChat([...chat, msg]);
    });
  }, []);

  const handleSubmit = (e) => {
    e.preventDefault();
    const userid = params.userid;
    const roomid = params.roomid;

    socket.emit("chat", { roomid, userid, message });
    setMessage([]);
    // console.log(message);
    // console.log(params.userid);
    // console.log(params.roomid);
  };
  return (
    <div>
      <h4>Collaboration Interface</h4>

      <table>
        {chat && (
          <>
            {chat.map((x, index) => {
              return (
                <>
                  <tr key={index}>
                    <td>{x.user}</td>
                    <td>{x.email}</td>
                    <td>
                      <input
                        name="user"
                        value={memberSelection}
                        type="checkbox"
                        onChange={(e) => {
                          setMemberSelection(e.target.checked);
                          const data = {
                            id: x.id,
                            email: x.email,
                            value: e.target.checked,
                          };
                          if (data.value === true) {
                            selectedUsers.push(data);
                          } else {
                            const filterdata = selectedUsers.filter(
                              (x) => x.id !== data.id && x.email !== data.email
                            );
                            setSelectedUsers(filterdata);
                          }
                        }}
                      />
                    </td>
                  </tr>
                </>
              );
            })}
          </>
        )}
      </table>

      <form>
        <div>
          {/* <label>Enter your message:</label> */}
          <input
            name="message"
            type="text"
            value={message}
            onChange={(e) => setMessage(e.target.value)}
          />
          <button onClick={handleSubmit}>Submit</button>
        </div>
      </form>
    </div>
  );
};

export default Chat;
