import React, { useState, useEffect } from "react";
import io from "socket.io-client";
import { useNavigate, useParams } from "react-router-dom";
import httpClient from "../httpClient";

const socket = io("http://127.0.0.1:5000/");

const Chat = () => {
  const [member, setMember] = useState([]);
  const [message, setMessage] = useState("");
  const [message1, setMessage1] = useState([]);
  const [chat, setChat] = useState([]);

  const params = useParams();

  const getRoomMemberId = async (id) => {
    try {
      const res = await httpClient.get(
        `//localhost:5000/contract/api/get_room_members/${id}`
      );

      const result = res.data.response;
      const filtered = result.filter((x) => x.user_id === params.userid);
      // console.log(filtered);
      setMember(filtered);
    } catch (error) {
      console.log("No room is available");
    }
  };

  useEffect(() => {
    getRoomMemberId(params.roomid);
  }, []);

  useEffect(() => {
    socket.on("chat", (payload) => {
      setChat([...chat, payload]);
    });

    socket.on("connect", function () {
      socket.emit("join_room", {
        user: params.userid,
        room: params.roomid,
      });
    });

    //join room announcement

    // socket.on("join_room_announcement", function (data) {
    //   console.log(message1);
    //   setMessage1([...message1, data.user]);
    //   // setMessage1([...message1, data.user]);
    //   // const data = `${payload.user} join the room`;
    //   // console.log(data);
    //   // setMessage1(data);
    // });
  });

  const handleSubmit = async (e) => {
    e.preventDefault();

    const roomid = params.roomid;
    getRoomMemberId(roomid);

    if (member) {
      // console.log(member);
      const roommemberid = member[0].id;
      socket.emit("chat", { roomid, roommemberid, message });
      setMessage("");
    }

    // try {
    //   const res = await httpClient.post(
    //     `//localhost:5000/contract/api/addmessage`,
    //     data
    //   );
    // } catch (error) {
    //   console.log("No message available");
    // }

    // setMessage([]);
    // window.location.reload(false);
  };
  return (
    <div>
      <h4>Collaboration Interface</h4>
      <table>
        {chat && (
          <>
            {chat.map((x, index) => {
              return (
                <>
                  <tr key={index}>
                    <td>
                      <p>
                        {x.roommemberid} : {x.message}
                      </p>
                    </td>
                  </tr>
                </>
              );
            })}
          </>
        )}
      </table>
      {member && <>
      </>}


    </div>
  );
};

export default Chat;
