import axios from "axios";
import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import httpClient from "../../httpClient";
const Register = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [stateErrors, setStateErrors] = useState({
    emailError: "",
    passwordError: "",
  });
  const navigate = useNavigate();

  // validation
  const validate = () => {
    let emailError = "";
    let passwordError = "";

    if (email === "") {
      emailError = "Email is required.";
    }

    if (password === "") {
      passwordError = "Password is required.";
    }

    if (password && password.length < 4) {
      passwordError = "Password length should be greator or equal 4.";
    }

    if (!email.includes("@")) {
      emailError = "Invalid email";
    }

    if (emailError || passwordError) {
      setStateErrors({
        emailError,
        passwordError,
      });
      return false;
    }
    return true;
  };

  const handleLogin = () => {
    navigate("/login");
  };

  const handleSubmit = async () => {
    const isValid = validate();

    if (isValid) {
      console.log("submit");
      try {
        const resp = await httpClient.post(
          "//localhost:5000/contract/api/register",
          {
            email,
            password,
          }
        );
        console.log("hi");
        if (resp) {
          console.log(resp);
          // clear form
          setEmail("");
          setPassword("");
          setStateErrors({ emailError: "", passwordError: "" });
          // navigate to login page
          // navigate("/login");
        }
      } catch (error) {
        if (error.response.status === 401) {
          alert("Invalid credentials");
        }
      }
    }
  };

  return (
    <div className="mb-3">
      <h3 className="mb-3">Register</h3>
      <form>
        <div class="row col-sm-4">
          <label for="inputEmail" class="col-sm-3 col-form-label">
            Email
          </label>
          <div class="col-sm-8">
            <input
              name="email"
              type="email"
              className="form-control form-control-sm"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
          </div>
        </div>
        {stateErrors.emailError && (
          <>
            <div class="row col-sm-4">
              <label for="inputEmail" class="col-sm-3 col-form-label"></label>
              <div class="col-sm-8">
                <span className="error">
                  {stateErrors.emailError && stateErrors.emailError}
                </span>
              </div>
            </div>
          </>
        )}

        <div class="row col-sm-4">
          <label for="inputPassword" class="col-sm-3 col-form-label">
            Password
          </label>
          <div class="col-sm-8">
            <input
              name="password"
              type="password"
              className="form-control form-control-sm"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
          </div>
        </div>

        {stateErrors.passwordError && (
          <>
            <div class="row col-sm-4">
              <label
                for="inputPassword"
                class="col-sm-3 col-form-label"
              ></label>
              <div class="col-sm-8">
                <span className="error">
                  {stateErrors.passwordError && stateErrors.passwordError}
                </span>
              </div>
            </div>
          </>
        )}

        <div class="row col-sm-4">
          <label for="inputEmail" class="col-sm-3 col-form-label"></label>
          <div class="col-sm-8">
            <button onClick={()=>handleSubmit} className="btn btn-sm btn-success">
              Register
            </button>
            <button onClick={handleLogin} className="btn btn-sm btn-success">
              Login
            </button>
          </div>
        </div>
      </form>
    </div>
  );
};

export default Register;
