import React, { useState, useEffect } from "react";
import io from "socket.io-client";
import { useNavigate, useParams } from "react-router-dom";
import httpClient from "../httpClient";

const socket = io("http://127.0.0.1:5000/");

const Chat = () => {
  const [member, setMember] = useState([]);
  const [message, setMessage] = useState("");
  const [message1, setMessage1] = useState([]);
  const [chat, setChat] = useState([]);

  const params = useParams();

  useEffect(() => {
    socket.on("connect", function () {
      socket.emit("join_room", {
        user: params.userid,
        room: params.roomid,
      });
    });

    //join room announcement

    socket.on("join_room_announcement", function (data) {
      console.log(data.user);
      setMessage1([])
      // const data = `${payload.user} join the room`;
      // console.log(data);
      // setMessage1(data);
    });

    socket.on("chat", (payload) => {
      // console.log(payload.data);
      setChat([...chat, payload.data]);
    });

    (async () => {
      try {
        const res = await httpClient.get(
          `//localhost:5000/contract/api/get_room_members/${params.roomid}`
        );
        // console.log(res.data.response);
        const result = res.data.response;
        const filtered = result.filter((x) => x.user_id === params.userid);
        setMember(filtered);
      } catch (error) {
        console.log("No room is available");
      }
    })();
    // (async () => {
    //   try {
    //     const res = await httpClient.get(`//localhost:5000/contract/api/chat`);
    //     // console.log(res.data.response);
    //     const result = res.data.response;
    //     setChat(result);
    //   } catch (error) {
    //     console.log("No room is available");
    //   }
    // })();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();

    const roomid = params.roomid;
    const roommemberid = member[0].id;

    const data = {
      room_id: roomid,
      room_member_id: roommemberid,
      message,
    };
    socket.emit("chat", { data });
    setMessage("");

    // try {
    //   const res = await httpClient.post(
    //     `//localhost:5000/contract/api/addmessage`,
    //     data
    //   );
    // } catch (error) {
    //   console.log("No message available");
    // }

    // setMessage([]);
    // window.location.reload(false);
  };
  return (
    <div>
      <h4>Collaboration Interface</h4>
      <div>{message1}</div>
      <table>
        {chat && (
          <>
            {chat.map((x, index) => {
              return (
                <>
                  <tr key={index}>
                    <td>
                      <p>
                        {x.room_member_id} :{x.message}
                      </p>
                    </td>
                  </tr>
                </>
              );
            })}
          </>
        )}
      </table>

      <form>
        <div>
          <input
            name="chat"
            type="text"
            placeholder="Enter your message"
            value={message}
            onChange={(e) => setMessage(e.target.value)}
          />
          <button onClick={handleSubmit}>Submit</button>
        </div>
      </form>
    </div>
  );
};

export default Chat;
